/**
 * Update each section/article if it is within the threshold
 * Show if it is, hide otherwise
 */
function update_sections() {
    // Iterate through each section OR articles (see examples.html) in the main
    $('main section, main section article').each(function () {
        // top and bottom of section
        var top = $(this).position().top;
        var bot = $(this).position().top + $(this).outerHeight();
        // thresholds are a smaller section of the window height to check if either ends of the section is in view
        // the middle third is the threshold
        var window_third = $(window).height() / 3;
        var top_threshold = $(window).scrollTop() + window_third;
        var bot_threshold = $(window).scrollTop() + (2 * window_third);

        /**
         * Check if all of the item is not in the threshold
         *  If it isn't, then you can assume the item IS in the threshold
         */
        ((top > bot_threshold && bot > bot_threshold) || (top < top_threshold && bot < top_threshold)) ?
            $(this).stop(true).animate({ 'opacity': '0' }, 500) : // show if true
            $(this).stop(true).animate({ 'opacity': '1' }, 500); // hide if false
    });
}

// https://codepen.io/annalarson/pen/AegVzq
// page ready
$(document).ready(function () {
    // set position to top
    $(window).scrollTop(0);

    // run at start of page once
    update_sections();
});

// on page scroll
$(window).scroll(function () {
    // run check on sections, now we know the scroll direction
    update_sections();
});
function toggleMenu() {
    $(".hamburger").toggleClass("is-active");

    //show when active
    if ($(".hamburger").hasClass("is-active")) {
        $("nav").slideDown();
        $(".burger").css("margin-left", $("nav").width());

        //dark background
        $("main").prepend(`<div class="black-out d-lg-none">`);
    }
    //hide when inactive
    else {
        $("nav").slideUp();
        $(".burger").css("margin-left", 0);

        //remove dark background
        $(".black-out").remove();
    }
}

$(document).ready(function () {
    //remove d-none from nav and keep hidden
    //this stops the menu from flashing on load
    $("nav").hide().removeClass("d-none");

    //on menu button click
    $(".hamburger").on("click", function (e) {
        toggleMenu();
    });
});

//https://stackoverflow.com/questions/1403615/use-jquery-to-hide-a-div-when-the-user-clicks-outside-of-it
$(document).mouseup(function (e) {
    //nav is visible (menu is active)
    //outside of nav (x position right of width)
    //not menu button
    if ($(".hamburger").hasClass("is-active") && e.pageX > $("nav").width() && !$(".burger").find("*").is(e.target)) {
        toggleMenu();
    }
});
$(function () { //on load
    const speedMax = 400; //ms
    const speedMin = 50;

    //https://thewebdev.info/2022/02/09/how-to-create-pause-or-delay-in-a-javascript-for-loop/
    const wait = ms => new Promise(resolve => setTimeout(resolve, ms));

    //https://stackoverflow.com/questions/4959975/generate-random-number-between-two-numbers-in-javascript
    const rand = (min, max) => Math.floor(Math.random() * (max - min + 1) + min);

    const typeWriter = async (e, txt) => {
        e.text("");
        for (const c of txt) {
            e.text(e.text() + c);
            await wait(rand(speedMin, speedMax));
        }
    }

    //Each typewriter element
    jQuery('.typewriter').each(function () {
        var element = $(this);
        const txt = element.text();
        typeWriter(element, txt);
    });
});
/**
 * Appends text to the given error field
 * @param {text} field automatically appends '_error'
 * @param {text} text 
 */
function error_text(field, text) {
    $(`#${field}_error`).text(text);

    // on error, make the field red
    if (text != "") {
        $(`#${field}`).addClass('required');
    }
    else {
        $(`#${field}`).removeClass('required');
    }
}

let valid_fields = [];
valid_fields['fname'] = false;
valid_fields['sname'] = false;
valid_fields['email'] = false;
valid_fields['subject'] = false;
valid_fields['message'] = false;

function validate_name(field) {
    value = $(`#${field}`).val();
    // check for any data
    if (value == "") {
        valid_fields[field] = false;
        error_text(field, "Please complete this field");
    }
    // check for special characters
    //https://www.regextester.com/93648
    else if (!value.match(/^[a-zA-Z]+(([',. -][a-zA-Z ])?[a-zA-Z]*)*$/)) {
        valid_fields[field] = false;
        error_text(field, "No special characters");
    }
    // valid
    else {
        valid_fields[field] = true;
        error_text(field, "");
    }
}

function validate_email() {
    value = $(`#email`).val();
    // check for any data
    if (value == "") {
        valid_fields['email'] = false;
        error_text('email', "Please complete this field");
    }
    // special characters
    else if (!value.match(/[A-Za-z0-9@]+$/)) {
        valid_fields['email'] = false;
        error_text('email', "No special characters");
    }
    // @
    else if (!value.match(/^[^@]+@[^@]+$/)) {
        valid_fields['email'] = false;
        error_text('email', "Must include only one @");
    }
    // split by @
    else {
        let chunks = value.split('@');
        let username = chunks[0];
        let domain = chunks[1];
        // username
        if (!username.match(/[A-Za-z0-9]+$/)) {
            valid_fields['email'] = false;
            error_text('email', "Please check the first part of your email");
        }
        // domain
        // https://stackoverflow.com/questions/21173734/extracting-top-level-and-second-level-domain-from-a-url-using-regex
        else if (!domain.match(/[^.]*\.[^.]{2,3}(?:\.[^.]{2,3})?$/)) {
            valid_fields['email'] = false;
            error_text('email', "Please check the second part of your email");
        }
        // valid
        else {
            valid_fields['email'] = true;
            error_text('email', "");
        }
    }
}

function validate_text(field) {
    value = $(`#${field}`).val();
    // check for any data
    if (value == "") {
        valid_fields[field] = false;
        error_text(field, "Please complete this field");
    }
    // check for special characters
    else if (!value.match(/[A-Za-z0-9 _\-.,!?"'()\r\n]*/)) {
        valid_fields[field] = false;
        error_text(field, "No special characters");
    }
    // valid
    else {
        valid_fields[field] = true;
        error_text(field, "");
    }
}

